import React, { useEffect, useState } from "react";
import Button from "./components/Button";
import CandidateCard from "./components/CandidateCard";
import axios from "axios";
import "./styles.css";

export default function App() {
  const [data, setData] = useState([]);
  const [loading,setLoading]=useState(false);
  const [error,setError]=useState(false);
 // const [salary,setSalary] =useState("asc");
  const[page,setPage]=useState(1)

useEffect(()=>{
  fetchData({page})
},[page])

const fetchData= async({page})=>{
  setLoading(true);
  axios({
    method:"GET",
    // url:"https://json-server-mocker-masai.herokuapp.com/candidates",
    url:"http://localhost:8080/candidates",
    params:{
      _page:page,
      _limit:5

    }

  })
  .then(res=>{
    setData(res.data);
    setLoading(false);
  })
  .catch(err=>{
   setError(true);
   setLoading(false);
  })
}

console.log(data)
console.log("page",page)
  return (
    <div className="App">
      <div>

    { loading  &&  <div id="loading-container">...Loading</div>} 
    {error && <div>Something went wrong!</div>}  
        <Button id="SORT_BUTTON" title={`Sort by Ascending Salary`} />
        <Button onClick={setPage}title="PREV" id="PREV" />
        <Button  onClick={setPage} id="NEXT" title="NEXT" />
      </div>
      {data.map((item) =>(
        <CandidateCard key={item.id} {...item}/>
      ))}
    </div>
  );
}
